mapper.select:
select * from(
select rownum rnum, no, title, writer, writeDate, hit
from(

select no, title, writer, writeDate, hit
from sellOut



order by no desc
)
)
where rnum between #{startRow} and #{endRow}

mapper.update:
update sellOut set title=#{title}, content=#{content}, writer=#{writer}, patid=#{patid}, patage=#{patage}, patgender=#{patgender}, patcolor=#{patcolor}
, fileName=#{fileName}
where no = #{no}

mapper.select:
select fileName
from sellOut
where no = #{no}
